<template>\n  <div class=\"service-card\" :class=\"{ 'is-provider': isProvider }\" @click=\"handleCardClick\">\n    <!-- Card Content -->\n    <div class=\"card-header\">\n      <h3 class=\"service-name\">{{ service.name }}</h3>\n      <span v-if=\"isProvider\" class=\"actions-btn\" @click.stop=\"toggleActions\">\n        ‚ãÆ\n      </span>\n      <span v-else class=\"bookmark-btn\" @click.stop=\"toggleSave\">\n        {{ isSaved ? '‚ù§Ô∏è' : 'ü§ç' }}\n      </span>\n    </div>\n\n    <!-- Provider Actions Menu -->\n    <div v-if=\"isProvider && showActions\" class=\"actions-menu\">\n      <button @click.stop=\"handleEdit\" class=\"action-btn edit-btn\">\n        <svg width=\"18\" height=\"18\" viewBox=\"0 0 24 24\" fill=\"none\" stroke=\"currentColor\">\n          <path d=\"M11 4H4a2 2 0 0 0-2 2v14a2 2 0 0 0 2 2h14a2 2 0 0 0 2-2v-7\"/>\n          <path d=\"M18.5 2.5a2.121 2.121 0 0 1 3 3L12 15l-4 1 1-4 9.5-9.5z\"/>\n        </svg>\n        –†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å\n      </button>\n      <button @click.stop=\"handleDelete\" class=\"action-btn delete-btn\">\n        <svg width=\"18\" height=\"18\" viewBox=\"0 0 24 24\" fill=\"none\" stroke=\"currentColor\">\n          <polyline points=\"3 6 5 6 21 6\"/>\n          <path d=\"M19 6v14a2 2 0 0 1-2 2H7a2 2 0 0 1-2-2V6m3 0V4a2 2 0 0 1 2-2h4a2 2 0 0 1 2 2v2\"/>\n          <line x1=\"10\" y1=\"11\" x2=\"10\" y2=\"17\"/>\n          <line x1=\"14\" y1=\"11\" x2=\"14\" y2=\"17\"/>\n        </svg>\n        –£–¥–∞–ª–∏—Ç—å\n      </button>\n    </div>\n\n    <!-- Card Info -->\n    <div v-if=\"!showActions\" class=\"card-info\">\n      <p class=\"description\">{{ service.description }}</p>\n      <div class=\"card-footer\">\n        <div class=\"price-category\">\n          <span class=\"category\">{{ service.category }}</span>\n          <span class=\"price\">{{ service.price }} ‚ÇΩ</span>\n        </div>\n        <span v-if=\"!isProvider\" class=\"view-more\">–ü–æ–¥—Ä–æ–±–Ω–µ–µ ‚Üí</span>\n      </div>\n    </div>\n  </div>\n</template>\n\n<script setup lang=\"ts\">\nimport { ref, computed } from 'vue'\n\ninterface Service {\n  id: string | number\n  name: string\n  price: number\n  description?: string\n  category?: string\n}\n\ninterface Props {\n  service: Service\n  isProvider?: boolean\n  isSaved?: boolean\n}\n\ninterface Emits {\n  (e: 'click'): void\n  (e: 'edit'): void\n  (e: 'delete'): void\n  (e: 'save'): void\n  (e: 'unsave'): void\n}\n\nconst props = withDefaults(defineProps<Props>(), {\n  isProvider: false,\n  isSaved: false\n})\n\nconst emit = defineEmits<Emits>()\n\nconst showActions = ref(false)\nconst saved = ref(props.isSaved)\n\nconst isSaved = computed({\n  get: () => saved.value,\n  set: (val) => {\n    saved.value = val\n  }\n})\n\nconst handleCardClick = () => {\n  if (!props.isProvider && !showActions.value) {\n    emit('click')\n  }\n}\n\nconst toggleActions = () => {\n  if (props.isProvider) {\n    showActions.value = !showActions.value\n  }\n}\n\nconst toggleSave = () => {\n  if (!props.isProvider) {\n    isSaved.value = !isSaved.value\n    if (isSaved.value) {\n      emit('save')\n    } else {\n      emit('unsave')\n    }\n  }\n}\n\nconst handleEdit = () => {\n  showActions.value = false\n  emit('edit')\n}\n\nconst handleDelete = () => {\n  showActions.value = false\n  if (confirm(`–í—ã —É–≤–µ—Ä–µ–Ω—ã —á—Ç–æ —Ö–æ—Ç–∏—Ç–µ —É–¥–∞–ª–∏—Ç—å \"${props.service.name}\"?`)) {\n    emit('delete')\n  }\n}\n</script>\n\n<style scoped>\n.service-card {\n  background: rgba(30, 41, 59, 0.5);\n  border: 1px solid rgba(148, 163, 184, 0.1);\n  border-radius: 12px;\n  padding: 1rem;\n  color: white;\n  transition: all 0.2s ease;\n  position: relative;\n}\n\n.service-card:not(.is-provider) {\n  cursor: pointer;\n}\n\n.service-card:not(.is-provider):hover {\n  background: rgba(30, 41, 59, 0.7);\n  border-color: rgba(59, 130, 246, 0.3);\n  transform: translateY(-2px);\n}\n\n.card-header {\n  display: flex;\n  justify-content: space-between;\n  align-items: center;\n  margin-bottom: 0.75rem;\n}\n\n.service-name {\n  margin: 0;\n  font-size: 1rem;\n  font-weight: 600;\n  color: white;\n  flex: 1;\n}\n\n.actions-btn,\n.bookmark-btn {\n  cursor: pointer;\n  font-size: 1.25rem;\n  transition: all 0.2s ease;\n  padding: 4px 8px;\n  border-radius: 6px;\n  flex-shrink: 0;\n}\n\n.actions-btn:hover {\n  background: rgba(148, 163, 184, 0.1);\n}\n\n.bookmark-btn:hover {\n  transform: scale(1.2);\n}\n\n/* Actions Menu */\n.actions-menu {\n  position: absolute;\n  top: 100%;\n  right: 0;\n  background: rgba(15, 23, 42, 0.95);\n  border: 1px solid rgba(148, 163, 184, 0.2);\n  border-radius: 8px;\n  overflow: hidden;\n  z-index: 50;\n  min-width: 180px;\n  box-shadow: 0 10px 25px rgba(0, 0, 0, 0.3);\n}\n\n.action-btn {\n  width: 100%;\n  padding: 0.75rem 1rem;\n  border: none;\n  background: none;\n  color: #94a3b8;\n  font-size: 0.875rem;\n  font-weight: 500;\n  text-align: left;\n  cursor: pointer;\n  display: flex;\n  align-items: center;\n  gap: 0.75rem;\n  transition: all 0.2s ease;\n  border-bottom: 1px solid rgba(148, 163, 184, 0.1);\n}\n\n.action-btn:last-child {\n  border-bottom: none;\n}\n\n.action-btn:hover {\n  background: rgba(148, 163, 184, 0.1);\n}\n\n.action-btn svg {\n  width: 18px;\n  height: 18px;\n}\n\n.edit-btn:hover {\n  color: #3b82f6;\n}\n\n.delete-btn:hover {\n  color: #ef4444;\n}\n\n/* Card Info */\n.card-info {\n  display: flex;\n  flex-direction: column;\n  gap: 0.75rem;\n}\n\n.description {\n  margin: 0;\n  font-size: 0.875rem;\n  color: rgba(226, 232, 240, 0.8);\n  line-height: 1.4;\n}\n\n.card-footer {\n  display: flex;\n  justify-content: space-between;\n  align-items: center;\n  padding-top: 0.75rem;\n  border-top: 1px solid rgba(148, 163, 184, 0.1);\n}\n\n.price-category {\n  display: flex;\n  gap: 0.75rem;\n  align-items: center;\n}\n\n.category {\n  display: inline-block;\n  padding: 0.25rem 0.5rem;\n  background: rgba(59, 130, 246, 0.1);\n  color: #3b82f6;\n  border-radius: 6px;\n  font-size: 0.7rem;\n  font-weight: 500;\n  text-transform: uppercase;\n}\n\n.price {\n  font-size: 1rem;\n  font-weight: 600;\n  color: #fbbf24;\n}\n\n.view-more {\n  font-size: 0.75rem;\n  color: #3b82f6;\n  font-weight: 600;\n}\n</style>\n", "_requires_user_approval": true}